class InfiniteRotation extends StatefulWidget {
  final Uint8List decodeBytes;
  final Duration duration;

  const InfiniteRotation({
    Key? key,
    required this.decodeBytes,
    this.duration = const Duration(seconds: 2),
  }) : super();

  @override
  State<InfiniteRotation> createState() => _InfiniteRotationState(decodeBytes);
}

class _InfiniteRotationState extends State<InfiniteRotation>
    with SingleTickerProviderStateMixin {
  late final AnimationController _controller;
  late final Animation<double> _rotation;

  _InfiniteRotationState(Uint8List decodeBytes);

  @override
  void initState() {
    super.initState();
    _controller = AnimationController(
      vsync: this,
      duration: widget.duration,
    )..repeat();
    _rotation = Tween(begin: 0.0, end: 1.0).animate(_controller);
  }

  @override
  void dispose() {
    _controller.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return SizedBox(
      width: ScreenAdapt.width(200),
      height: ScreenAdapt.height(200),
      child: AnimatedBuilder(
        animation: _controller,
        builder: (_, child) {
          return RotationTransition(
            turns: _rotation,
            child: SvgPicture.memory(
              widget.decodeBytes,
              fit: BoxFit.cover,
              width: ScreenAdapt.width(50),
              height: ScreenAdapt.width(50),
            ),
          );
        },
      ),
    );
  }
}

class CustomToast {
  static OverlayEntry? overlayEntry;
  static void show({
    required String msg,
    String iconType = 'loading',
    String image = '',
    bool mask = false,
    Duration duration = const Duration(seconds: 2),
  }) {
    dismiss();

    final context = Get.overlayContext;
    if (context == null) {
      debugPrint(
          'Toast Error: Get.overlayContext is null. Are you using GetMaterialApp?');
      return;
    }
    final overlay = Overlay.of(context);

    overlayEntry = OverlayEntry(
      builder: (_) => _ToastWidget(
        msg: msg,
        iconType: iconType,
        image: image,
        mask: mask,
      ),
    );

    overlay.insert(overlayEntry!);
    Future.delayed(duration, () {
      dismiss();
    });
  }

  static void dismiss() {
    overlayEntry?.remove();
    overlayEntry = null;
  }
}

